import { Box } from "@mui/material";
import React from "react";
import { appColor } from "../../../assets/Color";
import Logo from "../../../components/header/Logo";
import waiterImage from "../../../assets/waiter.png";

const Sidebar = ({ info, links }) => {
  return (
    <Box
      sx={{
        width: "300px",
        height: "100vh",
        background: `linear-gradient(to bottom, #333, ${appColor.yellow})`,
      }}
    >
      <Box
        sx={{
          margin: "10px auto",
          display: "inline-block",
          textAlign: "center",
          transform: "scale(0.8)",
        }}
      >
        <Logo />
      </Box>
      <Box
        sx={{
          textAlign: "center",
          margin: "20px",
          display: "flex",
          justifyContent: "center",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Box
          sx={{
            display: "inline-block",
            width: "130px",
            borderRadius: "50%",
            border: `1px solid ${appColor.yellow}`,
          }}
        >
          <img style={{ maxWidth: "95%" }} src={waiterImage} />
        </Box>
        <Box sx={{ fontWeight: "600" }}>John</Box>
      </Box>
    </Box>
  );
};

export default Sidebar;
